import React, { Component } from 'react'
import '../css/navbar.css'

export default class Navbar extends Component {
    constructor(props) {
        super(props)
        this.state = {
            active: "sorting"
        }
        this.changeTab = this.changeTab.bind(this)
        this.changePage = this.changePage.bind(this)
    }

    changeTab(e) {
        e.preventDefault()
        this.setState(state => ({
            active: e.target.id
        }))
        this.props.changePage(this.state.active)
    }


    render() {
        return ( 
        <div className="navbar">
            <div className="nav-item" ><a className={`${this.state.active==="sorting" ? "active" : ""}`} href="" id="sorting" onClick={this.changeTab}>Sorting</a></div>
            <div className="nav-item" ><a className={`${this.state.active==="graphs" ? "active" : ""}`} href="" id="graphs" onClick={this.changeTab}>Graphs</a></div>
            <div className="nav-item" ><a className={`${this.state.active==="path-finding" ? "active" : ""}`} href="" id="path-finding" onClick={this.changeTab}>Path Finding</a></div>
        </div>
        )
    }
}
